mapper.select:
select um.user_code,
um.user_name, um.user_email, um.user_id, um.user_password, um.user_roles, um.user_provider,
ud.user_profile_img, ud.user_address, ud.user_phone, ud.user_gender from user_mst um left outer join user_dtl ud on(ud.user_code = um.user_code) where um.user_id = #{username}

select um.user_code,
um.user_name, um.user_email, um.user_id, um.oauth2_id, um.user_password, um.user_roles, um.user_provider,
ud.user_profile_img, ud.user_address, ud.user_phone, ud.user_gender from user_mst um left outer join user_dtl ud on(ud.user_code = um.user_code) where um.oauth2_id = #{oauth2_id}
